mapper.update:
UPDATE CART_INFO 
   SET STATUS = '01'
 WHERE CART_NO = #{cartNo}

mapper.insert:
INSERT INTO PAYMENT_INFO(
	  cart_no
	, member_num
	, store_code
	, payment_amt
	, tid
	, status
	, pay_method
	, pay_date
)
VALUES(
	  #{cartNo}
	, #{memberNum}
	, #{storeCode}
	, #{paymentAmt}
	, #{tid}
	, 'O1'
	, #{payMethod}
	, SYSDATE
)
